mapper.select:
select list.* from
(
select @rownum := @rownum + 1 as rownum, b.* from
(select * from articles order by article_no desc) b, (select @rownum := 0) r
) list where list.rownum between #{beginRow} and #{endRow}

select list.*
from (
select @rownum := @rownum + 1 as rownum, b.* from
(select * from articles where category=#{category} order by article_no desc) b,
(select @rownum := 0) r
) list where list.rownum between #{page.beginRow} and
#{page.endRow}
