mapper.select:
select count(*) from board

	
		
			category=#{category} and division=#{division}
		
		
			category=#{category}
		
		
			division=#{division}
		
		
			writer=#{writer}
		
		
			article_no=-1

		select articles.* from (
			select @rownum := @rownum + 1 as rownum, b.* from
			(select * from board order by article_no desc) b, (select @rownum := 0) r
        ) articles where articles.rownum between #{beginRow} and #{endRow}

		select articles.* from (
			select @rownum := @rownum + 1 as rownum, b.* from
			(select * from board where category=#{category} order by article_no desc) b, (select @rownum := 0) r
        ) articles where articles.rownum between #{page.beginRow} and #{page.endRow}

		select articles.* from (
			select @rownum := @rownum + 1 as rownum, b.* from
			(select * from board where category=#{category} and division=#{division} 
			order by article_no desc) b, (select @rownum := 0) r
        ) articles where articles.rownum between #{page.beginRow} and #{page.endRow}
